function sendEvent(e, t, n, a) {
    a ? ga("send", "event", {
        eventCategory: e,
        eventAction: t,
        eventLabel: n,
        eventValue: a
    }) : n ? ga("send", "event", {
        eventCategory: e,
        eventAction: t,
        eventLabel: n
    }) : ga("send", "event", {
        eventCategory: e,
        eventAction: t
    });
}

function numberWithCommas(e) {
    return e.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

function shuffle(e) {
    for (var t, n, a = e.length; 0 !== a; ) n = Math.floor(Math.random() * a), t = e[a -= 1], 
    e[a] = e[n], e[n] = t;
    return e;
}

